def ary_length(data)
    puts "Il y a #{data.length} handle dans cette array."
end

def ary_min(data)
    min = data.min{|a, b| a.length <=> b.length}
    puts "L'handle le plus court est #{min}"
end

def ary_five(data)
    a = 0
    data.each do |i|
        if i.length == 6
            a += 1
        end
    end
    puts "Il y a #{a} handles contenant 5 caractères"
        
end

def ary_maj(data)
    a = 0
    data.each do |i|
        if i[1] == i[1].upcase
            a += 1
        end
    end
    puts "Il y a #{a} handles commençant par une majuscule."
end

def ary_alpa(data)
    puts "#{data.sort.join(", ")}"
end

def ary_grow(data)
    data.sort_by! {|i| i.size}
    puts "#{data}"
end

def ary_epenser(data)
    puts "@epenser se trouve à la #{data.index("@epenser")}ème position."
end

def ary_répartion(data)
    a = data.max {|a,b| a.length <=> b.length}
    
    (a.length+1).times do |i|
        ary(data,i)
    end
end

def ary(data,i)
    a = 0
    data.each do |f|
        if f.length == i
            a += 1
        end
    end
    if a != 0
        puts"Il y a #{a} handles avec #{i} caractères"
    end
end


def perform
    data = datav

    puts "1.Nombre de handles\n
    2.Handle le plus court\n
    3.Handles avec 5 caractères\n
    4.Nombres d'handles avec une majuscule\n
    5.Trier par ordre alphabétique\n
    6.Trier par taille\n
    7.Posiiton de @epenser\n
    8.Répartition des handles\n
    Choisissez le mode :\n>"
    
    mode = gets.chomp
    
    case
    
    when mode = 1 then ary_length(data)
    when mode = 2 then ary_min (data)
    when mode = 3 then ary_five(data)
    when mode = 4 then ary_maj(data)
    when mode = 5 then ary_alpa(data)
    when mode = 6 then data = ary_grow(data)
    when mode = 7 then ary_epenser(data)
    when mode = 8 then ary_répartion(data)
    
    end  
end

perform